import React, { useState } from 'react';
import apiClient from '../api'; // Import API client

function Login() {
    const [username, setUsername] = useState(''); // current value of the state, method to update the value
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        // clears error messages and does not refresh every submission
        e.preventDefault(); 
        setError('');

        try {
            const response = await apiClient.post('/token/', {
                username: username,
                password: password,
            });
            // Save the token to the browser's local storage
            localStorage.setItem('accessToken', response.data.access);
            alert('Login successful!');
            // Here you would typically redirect the user to the main app page
        } catch (err) {
            setError('Login failed. Please check your credentials.');
            console.error(err);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>Username:</label>
                <input type="text" value={username} onChange={e => setUsername(e.target.value)} />
            </div>
            <div>
                <label>Password:</label>
                <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
            </div>
            <button type="submit">Login</button>
        </form>
    );
}

export default Login;
