import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // 1. Import useNavigate
import apiClient from '../api';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // 2. Get the navigate function

    const handleSubmit = async (e) => {
        e.preventDefault(); 
        setError('');
        try {
            const response = await apiClient.post('/token/', {
                username: username,
                password: password,
            });
            localStorage.setItem('accessToken', response.data.access);

            // 3. Replace the alert with the navigate function
            navigate('/home'); 

        } catch (err) {
            setError('Login failed. Please check your credentials.');
            console.error(err);
        }
    };

    return (
        // Note: The wrapping div from the CSS fix is not shown here,
        // but you should include it as described above.
        <form onSubmit={handleSubmit}>
            <h2>Login</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>Username:</label>
                <input type="text" value={username} onChange={e => setUsername(e.target.value)} />
            </div>
            <div>
                <label>Password:</label>
                <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
            </div>
            <button type="submit">Login</button>
        </form>
    );
}

export default Login;
