import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import apiClient from '../api';

function HomePage() {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState([]);
    const [searchType, setSearchType] = useState('artist'); // 'artist' or 'album'

    const handleSearch = async (e) => {
        e.preventDefault();
        try {
            const response = await apiClient.get(`/spotify/search/?q=${query}&type=${searchType}`);
            // The response structure is different for artists vs. albums
            setResults(response.data[searchType === 'artist' ? 'artists' : 'albums'].items);
        } catch (error) {
            console.error("Search failed:", error);
        }
    };

    return (
        <div>
            <h1>Search Spotify</h1>
            <form onSubmit={handleSearch}>
                <input 
                    type="text" 
                    value={query} 
                    onChange={(e) => setQuery(e.target.value)} 
                    placeholder="Search for artists or albums" 
                />
                <select value={searchType} onChange={(e) => setSearchType(e.target.value)}>
                    <option value="artist">Artist</option>
                    <option value="album">Album</option>
                </select>
                <button type="submit">Search</button>
            </form>

            <div>
                {results.map((item) => (
                    <div key={item.id}>
                        {/* Link to the album detail page */}
                        <Link to={`/album/${item.id}`}>
                            <p>{item.name}</p>
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HomePage;
